import { Link } from "react-router-dom";
import { useMutation, useQueryClient } from "@tanstack/react-query";

import { useVendorOrders } from "@/hooks/useVendorOrders";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { api } from "@/lib/api";

const dateFormatter = new Intl.DateTimeFormat("en-US", { dateStyle: "medium" });

function statusVariant(status: string) {
  const normalized = status.toUpperCase();
  if (normalized === "OPEN") return "default";
  if (normalized === "PARTIAL") return "secondary";
  if (normalized === "CANCELLED") return "destructive";
  return "outline";
}

export default function VendorOrdersPage() {
  const { toast } = useToast();
  const qc = useQueryClient();
  const ordersQuery = useVendorOrders();
  const approveMutation = useMutation({
    mutationFn: async (orderId: string) => {
      await api.patch(`/procurement/vendor/pos/${orderId}/ack`);
    },
    onSuccess: () => {
      qc.invalidateQueries({ queryKey: ["vendor", "orders"] });
      toast({ title: "Order approved", description: "The hospital has been notified." });
    },
    onError: (err: any) => {
      const message = err?.response?.data?.error || err.message || "Failed to approve order";
      toast({ title: "Approval failed", description: message, variant: "destructive" });
    },
  });

  return (
    <section className="space-y-6">
      <header className="space-y-2">
        <h1 className="text-3xl font-semibold tracking-tight">Purchase orders</h1>
        <p className="text-muted-foreground max-w-3xl">
          Review purchase orders generated by hospital low-stock alerts. Acknowledge and prepare shipments based on the
          latest PO status.
        </p>
      </header>

      {ordersQuery.isError ? (
        <Alert variant="destructive">
          <AlertDescription>We couldn&apos;t load your orders right now. Please try again.</AlertDescription>
        </Alert>
      ) : null}

      <Card>
        <CardHeader>
          <CardTitle>Open orders</CardTitle>
          <CardDescription>Showing the most recent purchase orders assigned to your account.</CardDescription>
        </CardHeader>
        <CardContent>
          {ordersQuery.isLoading ? (
            <div className="text-sm text-muted-foreground">Loading orders...</div>
          ) : ordersQuery.data && ordersQuery.data.length > 0 ? (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>PO #</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Vendor approval</TableHead>
                  <TableHead>Ordered</TableHead>
                  <TableHead>Items</TableHead>
                  <TableHead>Total qty</TableHead>
                  <TableHead className="text-right">Action</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {ordersQuery.data.map((order) => (
                  <TableRow key={order.id}>
                    <TableCell className="font-medium">{order.poNo}</TableCell>
                    <TableCell>
                      <Badge variant={statusVariant(order.status)}>{order.status}</Badge>
                    </TableCell>
                    <TableCell>
                      {order.vendorAcknowledgedAt ? (
                        <Badge variant="secondary">Approved</Badge>
                      ) : (
                        <Badge variant="outline">Pending</Badge>
                      )}
                    </TableCell>
                    <TableCell>{dateFormatter.format(new Date(order.orderedAt))}</TableCell>
                    <TableCell>
                      <div className="text-sm font-medium">{order.lineCount}</div>
                      {order.linesPreview && order.linesPreview.length ? (
                        <div className="mt-1 space-y-1 text-xs text-muted-foreground">
                          {order.linesPreview.map((line) => (
                            <div key={line.id}>
                              <span className="font-medium text-foreground/80">
                                {line.itemName ?? `Item #${line.itemId}`}
                              </span>
                              {line.itemStrength ? ` ${line.itemStrength}` : ""}
                              {line.itemType ? ` (${line.itemType})` : ""}
                            </div>
                          ))}
                        </div>
                      ) : null}
                    </TableCell>
                    <TableCell>{order.totalQty}</TableCell>
                    <TableCell className="text-right space-x-2">
                      {!order.vendorAcknowledgedAt ? (
                        <Button
                          size="sm"
                          variant="secondary"
                          disabled={approveMutation.isPending}
                          onClick={() => approveMutation.mutate(order.id)}
                        >
                          Approve
                        </Button>
                      ) : null}
                      <Button asChild variant="outline" size="sm">
                        <Link to={`/vendor/orders/${order.id}`}>View</Link>
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          ) : (
            <div className="text-sm text-muted-foreground">
              No purchase orders yet. Orders generated from low-stock alerts will appear here.
            </div>
          )}
        </CardContent>
      </Card>
    </section>
  );
}
